package com.collections.com;

public class Addmethodsample {
	public static int myadd(int x, int y) {
		int sum= x+y;
		return sum;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 8;
		int b=4;
		int sum=myadd(a,b);
		System.out.println("the sum is " +sum);

	}

}
