package com.operators;

public class Exampleoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//there are different, types of operators 
		/*
		 * arithimetic operators, assignment operators comparision operators logical
		 * operators bitwise operators
		 */
		int a =  4;
		int b = 5;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b); // division will give the quotient as output
		System.out.println(a%b); // modulo will give the remainder as output 
		/*
		 * int x=6; ++x; // x=x+1 System.out.println(x);
		 */
		int y=7;
		y--;  
		System.out.println(y);
	}

}
