package loopstatements;

public class Dowhileexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=6;
		do {
			System.out.println("jitendra" +i);
			i++;
		}while(i<=5);

	}

}
// while will  check the condition and gets executed 
//Do while will get executed atleast once
